  .stroke(color)
  .fill(color)
  .strokeDasharray("2 1")
  .strokeDashoffset(0.1)
  .strokeWidth(2);

svg.polyline(10, 5, 6.5, 3, 10, 1)
  .stroke(color)
  .fill("none")
  .strokeWidth(1);

svg.curve(9, 3, 17.5, 14)
  .stroke(color)
  .fill("none")
  .bend(0.6)
